    <p> Sp. Attack {pokedex[0].base["Sp. Attack"]}</p>
    <p>Sp. Defense {pokedex[0].base["Sp. Defense"]}</p>
    <p>Speed {pokedex[0].base.Speed}</p>  
    </div>
    <div className="Sprite">

       </div>

  </div>
  );
}

export default App;
